mapper.insert:
insert into address_book (
id,
user_id,
consignee, sex, phone, province_code, province_name, city_code, city_name, district_code, district_name, detail, label
,is_default
) VALUES (
#{id},
#{userId},
#{consignee}, #{sex}, #{phone}, #{provinceCode}, #{provinceName}, #{cityCode}, #{cityName}, #{districtCode}, #{districtName}, #{detail}, #{label}
,#{isDefault}
)

mapper.update:
update address_book set
user_id = #{userId},
consignee = #{consignee}, sex = #{sex}, phone = #{phone}, province_code = #{provinceCode}, province_name = #{provinceName}, city_code = #{cityCode}, city_name = #{cityName}, district_code = #{districtCode}, district_name = #{districtName}, detail = #{detail}, label = #{label}
,is_default = #{isDefault}
where id=#{id}
