mapper.select:
select * from shopping_cart where user_id = #{userId} AND

    dish_id=#{shoppingCartDTO.dishId}


    setmeal_id = #{shoppingCartDTO.setmealId}

mapper.delete:
delete from shopping_cart where user_id=#{userId}

    and dish_id = #{dishId}


    and setmeal_id = #{setmealId}

mapper.update:
update shopping_cart set number = #{number} where user_id=#{userId}

    and dish_id = #{dishId}


    and setmeal_id = #{setmealId}
